    current_line = []

    for word in words:
        # Check if adding this word would exceed the max length
        if sum(len(w) for w in current_line) + len(word) + len(current_line) - 1 < max_length:
            current_line.append(word)
        else:
            # Join the current line into a string and add to lines
            lines.append(' '.join(current_line))
            current_line = [word]  # Start a new line with the current word

    # Add the last line if there are any words left
    if current_line:
        lines.append(' '.join(current_line))

    return lines

